import React from 'react';
import './App.css';
import Table from "./Table.jsx";
import PropTypes from 'prop-types';
import { createMuiTheme, MuiThemeProvider, withStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/icons/Menu';
import AccountCircle from '@material-ui/icons/AccountCircle';
import MenuItem from '@material-ui/core/MenuItem';
import Menu from '@material-ui/core/Menu';
import Drawer from '@material-ui/core/Drawer';
import Button from '@material-ui/core/Button';
import List from '@material-ui/core/List';
import Divider from '@material-ui/core/Divider';
import Grid from '@material-ui/core/Grid';
import Chip from '@material-ui/core/Chip';
import { PacmanLoader } from 'react-spinners';
// import Paper from '@material-ui/core/Paper';
import { drawerListItems, otherListItems } from './drawerList';
import Webcam from "react-webcam";

import Amplify, { API, Storage } from 'aws-amplify';
import aws_exports from './aws-exports';
// import { withAuthenticator } from 'aws-amplify-react';

Amplify.configure(aws_exports);
var input_pic_name;
var time;
var timestamp;

async function detectFaces(bytes){
  const apiName = "api6832f3b1";
  const path = "/items"
  time = Date.now();
  timestamp = Math.floor(time / 1000);
  input_pic_name = timestamp.toString();

  await Storage.put(input_pic_name + '.jpg', bytes)
  .then (result => console.log(result))
  .catch(err => console.log(err));

  const headers = {
    "Content-Type": "application/json",
  };
  const body = {
    "image": input_pic_name + '.jpg'
  };
  console.log(body);

  const init = {
   body: body,
   headers: headers
 };
 console.log(init);

 return await API.post(apiName, path, init);
}

const new_font_theme = createMuiTheme({
  typography: {
    useNextVariants: true,
  },
});
// stylesheet of every component
const styles = theme => ({
  root: {
    flexGrow: 1,
    // height: 440,
    zIndex: 1,
    overflow: 'hidden',
    position: 'relative',
    display: 'flex',
    useNextVariants: true,
  },
  grow: {
    flexGrow: 1,
  },
  menuButton: {
    marginLeft: -12,
    marginRight: 20,
  },
  appBar: {
    backgroundColor: '#000000',
    zIndex: theme.zIndex.drawer + 1,
  },
  drawerPaper: {
    position: 'relative',
    width: 240,
  },
  list: {
    width: 250,
  },
  fullList: {
    width: 'auto',
  },
  content: {
    flexGrow: 1,
    // backgroundColor: theme.palette.background.default,
    padding: theme.spacing.unit * 3,
    minWidth: 0, // So the Typography noWrap works
  },
  toolbar: {
    display: 'flex',
    alignItems: 'center',
    // justifyContent: 'flex-end',
    padding: '0 8px',
    marginLeft: 'auto',
    marginRight: 'auto',
    ...theme.mixins.toolbar,
  },
  videoFrame: {
    // margin: 'auto',
    display: 'block',
    maxWidth: '100%',
    maxHeight: '100%',
  },
  button: {
   margin: theme.spacing.unit * 4,
   textTransform: 'none',
   fontFamily: [
      '-apple-system',
      'BlinkMacSystemFont',
      '"Segoe UI"',
      'Roboto',
      '"Helvetica Neue"',
      'Arial',
      'sans-serif',
